class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: int
        """
        char_set = set(s)
        result = 0
        odd_label = False
        for char in char_set:
            char_num = s.count(char)
            if char_num % 2 ==0:
                result += char_num
            else:
                odd_label = True
                result += char_num - 1
        if odd_label:
            result += 1
        return result

run = Solution()
s = ""
print(run.longestPalindrome(s))